'use strict';

const communis = {
  fascisMethods: {
    methodA:() => undefined,
  },
  operatiMethods: {},
  closureForFn: {},
  thisForFn: {},
};

const artus = (fn) => {

  const exitus = function(...args) {

    const bfn = fn.bind(communis.thisForFn);

    const propertiesOperati = {
      bfn: bfn,
      args: args,
      closureForFn: communis.closureForFn,
      thisForFn: communis.thisForFn,
    };

    exitus.getOperati = () => propertiesOperati;

    communis.closureForFn = communis.fascisMethods.methodA(communis.closureForFn);

  };
  exitus.fn = fn;
  return Object.assign(exitus, communis.operatiMethods);

};
module.exports = { communis, artus };
