 'use strict';

const { generativusFucus } = require('artus');

process.start = Date.now();

const nd = (str) => {
  console.log(str, Date.now() - process.start);
};

 generativusFucus.propObj.returnBfn = (bfn, closureForFn) => {
  nd(`kill ${closureForFn.flags.kill}`);
  if (!closureForFn.flags.kill) {
    return bfn;
  } else return () => undefined;
};

 generativusFucus.propObj.comutClosure = (closureForFn) => {
  if (closureForFn.context.counter >= closureForFn.context.max) {
    closureForFn.flags.kill = true;
  } else closureForFn.context.counter++;
  return closureForFn;
};

 generativusFucus.propObj.methods = {
  printFn() {
    console.log(propObj.closureForFn.bfn);
    return this;
  },
  limit(max = 0) {
    generativusFucus.propObj.closureForFn.context.max = max;
    return this;
  },
  timer(msec = 0) {
    const timer_ = setTimeout(() => {
      generativusFucus.propObj.closureForFn.flags.kill = true;
      timer_.close();
    }, msec);
    return this;
  },
  cancel() {
    generativusFucus.propObj.closureForFn.flags.kill = true;
  },
  resume() {
    generativusFucus.propObj.closureForFn.flags.kill = false;
  },
  throttle(msec) {
    generativusFucus.propObj.closureForFn.timer.interval = setInterval(() => {
      generativusFucus.propObj.closureForFn.context.counter = 0;
      generativusFucus.propObj.closureForFn.flags.kill = false;
      nd(`trot ${generativusFucus.propObj.closureForFn.context.counter}`);
    }, msec);
    return this;
  }
};

const fn = function(...args) {
  console.dir({ args, this: this });
};

// Use

const fucus = generativusFucus(generativusFucus.propObj, fn).limit(3);
fucus.throttle(500);

let count = 1;
const interval1 = setInterval(() => {
  nd(`call ${count}`);
  fucus(count++);
}, 100);

const timer1 = setTimeout(() => {
  timer1.close();
  interval1.close();
  generativusFucus.propObj.closureForFn.timer.interval.close();
}, 1000);

