'use strict';

const communis = {
  fascisMethod: () => undefined,
  operatiMethods: {},
  closureForFn: {},
  thisForFn: {},
};

const artus = (fn) => {

  const exitus = function(...args) {

    const bfn = fn.bind(communis.thisForFn);

    const properties = {
      bfn: bfn,
      args: args,
      closureForFn: communis.closureForFn,
      thisForFn: communis.thisForFn,
    };

    exitus.getOperati = () => properties;

    communis.closureForFn = communis.fascisMethod(communis.closureForFn);

  };
  exitus.fn = fn;
  return Object.assign(exitus, communis.operatiMethods);

};
module.exports = { communis, artus };
