'use strict';

const injectProtoFucus = (fn) => {
  const ProtoFucus = function() {
  };

  const fucus = (...args) => {
    fn(...args);
  };
  const fields = {
    otherFields: null,
  };

  fucus.fields = fields;

  fucus.addPrototype = function(name = '', proto) {
    ProtoFucus.prototype[name] = proto;
  };

  Object.setPrototypeOf(fucus, ProtoFucus.prototype);
  return Object.assign(fucus, fields);
};

module.exports = { injectProtoFucus };
